	CastOptions.Targ.party2 = 1;
else
	CastOptions.Targ.party2 = 0;
end

if ( keystate == "down" ) then
	CastOptions.Targ.party3 = 1;
else
	CastOptions.Targ.party3 = 0;
end

if ( keystate == "down" ) then
	CastOptions.Targ.party4 = 1;
else
	CastOptions.Targ.party4 = 0;
end

if ( keystate == "down" ) then
	CastOptions.Aimed = 1;
else
	CastOptions.Aimed = 0;
end
